import React, { Fragment } from "react";
import Planet from './planet'

async function getPlanets() {
    let response = await fetch('http://localhost:3000/api/planets.json')
    let data = await response.json()
    return data;
}
/* obs: Foi colocado vários arquivos na pasta 'public' e 'api' e tá sendo importado de lá*/

class Planets extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            planets: [ ] /* Foi removido os planetas daqui, pq foi implando do arquivo da pasta 'api' o marte, plutão e urano*/
        }
    }

    componentDidMount() {
        getPlanets().then(data => {
            this.setState(state => ({
                planets: data['planets']
            }))
        })
    }

    removeLast = () => {
        let new_planets = [...this.state.planets]
        new_planets.pop()
        this.setState(state => ({
            planets: new_planets
        }))
    }

    duplicateLastPlanet = () => {
        let last_planet=this.state.planets[this.state.planets.length - 1]
        this.setState(state => ({
            planets:[...this.state.planets,last_planet]
        }))
    }

    render() {
        return (
            <Fragment>
                <h3>Planet List</h3>
                <button onClick={this.removeLast}>Remove Last</button>
                <button onClick={this.duplicateLastPlanet}>Duplicate Last</button>
                <hr />
                {this.state.planets.map((planet, index) =>
                    <Planet
                        id={planet.id}
                        name={planet.name}
                        description={planet.description}
                        img_url={planet.img_url}
                        link={planet.link}
                        key={index}
                    />
                )}
            </Fragment>
        )
    }
}

export default Planets;
